import React from "react"; 
import ReactDOM from "react-dom"; 
import "./index.css"; 


class VimeoPage extends React.Component {
    render() {
        return (
            <div className= "content">
                <div className= "white-box">
                    <div className= "white-box-contents">
                        <img className= "white-box-img" src= "./monsoon.jpg"></img>
                        <div className= "white-box-text">
                            <h1>MONSOON III</h1>
                            <p>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Donec tincidunt ipsum augue. In faucibus vehicula magna
                            pulvinar aliquam. Cras aliquam feugiat lorem non auctor. 
                            Quisque sed lorem egestas mauris venenatis commodo eu id
                            nibh. Ut porta libero sed semper faucibus.
                            </p>

                        </div>
                    </div>
                </div>
                <div className= "gray-box">
                    <div className= "gray-box-contents">
                        <img className= "gray-box-img" src= "./beams.jpg"></img>
                        <div className= "gray-box-text">
                            <h1>BEAMS</h1>
                            <p>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Donec tincidunt ipsum augue. In faucibus vehicula magna
                            pulvinar aliquam. Cras aliquam feugiat lorem non auctor. 
                            Quisque sed lorem egestas mauris venenatis commodo eu id
                            nibh. Ut porta libero sed semper faucibus.
                            </p>
                        </div> 
                    </div>
                </div>
                <div className= "gradient-box">
                    <div className= "gradient-box-contents">
                        <img className= "gradient-box-img" src= "./move2.jpg"></img>
                        <div className= "gradient-box-text">
                            <h1>Move 2</h1>
                            <p>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                            Donec tincidunt ipsum augue. In faucibus vehicula magna
                            pulvinar aliquam. Cras aliquam feugiat lorem non auctor. 
                            Quisque sed lorem egestas mauris venenatis commodo eu id
                            nibh. Ut porta libero sed semper faucibus.
                            </p>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

//----------------------------------CAROUSEL----------------------------------
class Slider extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            images: [
                "./huntforthe.jpg",
                "./nichts.jpg", 
                "./viceversa.jpg",
                "./fourthphase.jpg", 
                "./fullmoon.jpg"
            ], 
            currentIndex: 0, 
            translateValue: 0
        }
    }

    goToPrevSlide = () => {
        if(this.state.currentIndex === 0) { //the first slide
            return; //cannot go to previous slide, TRY TO CHANGE!!!
        }
        this.setState(prevState => ({
            currentIndex: prevState.currentIndex - 1, //goes down
            translateValue: prevState.translateValue + this.slideWidth() 
        }))
    }

    goToNextSlide = () => {
        if(this.state.currentIndex === this.state.images.length - 1) {
            return this.setState({
                currentIndex: 0, 
                translateValue: 0
            })
        }
        this.setState(prevState => ({
            currentIndex: prevState.currentIndex + 1, 
            translateValue: prevState.translateValue + -(this.slideWidth())
        })); 
    }

    slideWidth = () => {
        return document.querySelector('.slide').clientWidth
    }

    render() {
        return (
            <div className= "slider">
                <div className= "slider-wrapper"
                    style= {{
                        transform: `translateX(${this.state.translateValue}px)`,
                        transition: "transform ease-out 0.3s"
                    }}>
                    {
                        this.state.images.map((image, i) => (
                            <Slide key={i} image={image}/>,
                            <Info key={i} image={image}/>
                        ))
                    } 
                </div>
                <LeftArrow goToPrevSlide= {this.goToPrevSlide}/>
                <RightArrow goToNextSlide= {this.goToNextSlide}/>
            </div>  
        )
    }
}

const Slide = ({image}) => {
    const styles = {
        backgroundImage: `url(${image})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: '100% 50%'
    }
    return <div className="slide" style={styles}></div>
}

const Info = ({image}) => {
    if (image == "./huntforthe.jpg") {
        return (
            <div className= "info">
                <img src= "./huntforthe.jpg"></img>
                <h1>Hunt for the Wilderpeople</h1>
                <p>
                    Raised on hip-hop and foster care, defiant city kid Ricky
                    gets a fresh start in the New Zealand countryside. From the
                    director of What We Do In The Shadows. 
                </p>
                <button className= "buy">
                    <img src= "./whitevimeo.png"></img>
                    <p>Buy Now</p>
                </button>
                <button className= "trailer">Watch Trailer</button>
            </div>
        )
    } else if (image == "./nichts.jpg") {
        return (
            <div className= "info">
                <img src= "./nichts.jpg"></img>
                <h1>Nichts passiert/A Decent Man</h1>
                <p>
                    A Swiss family takes a ski vacation and runs into trouble 
                    when the father, the titular decent man, finds himself in a
                    series of moral quandaries. 
                </p>
                <button className= "buy">
                    <img src= "./whitevimeo.png"></img>Buy Now
                </button>
                <button className= "trailer">Watch Trailer</button>
            </div>
        )
    } else if (image == "./viceversa.jpg") {
        return (
            <div className= "info">
                <img src= "./viceversa.jpg"></img>
                <h1>Vice Versa</h1>
                <p>
                    Come along with the Good Company crew as they travel 
                    throughout the US, Japan, BC and Quebec to showcase skiing
                    in the best way possible. 
                </p>
                <button className= "buy">
                    <img src= "./whitevimeo.png"></img>Buy Now
                </button>
                <button className= "trailer">Watch Trailer</button>
            </div>
        )
    } else if (image == "./fourthphase.jpg") {
        return (
            <div className= "info">
                <img src= "./fourthphase.jpg"></img>
                <h1>The Fourth Phase</h1>
                <p>
                    From the creators of The Art of FLIGHT, Red Bull Media House
                    presents THE FOURTH PHASE, a snowboarding epic starring 
                    iconic athlete Travis Rice. 
                </p>
                <button className= "buy">
                    <img src= "./whitevimeo.png"></img>Buy Now
                </button>
                <button className= "trailer">Watch Trailer</button>
            </div>
        )
    } else if (image == "./fullmoon.jpg") {
        return (
            <div className= "info">
                <img src= "./fullmoon.jpg"></img>
                <h1>Full Moon</h1>
                <p>
                    Be inspired by women who push boundaries. FULL MOON 
                    showcases the legends, current icons and future prodigies
                    of this ever-evolving lifestyle sport. 
                </p>
                <button className= "buy">
                    <img src= "./whitevimeo.png"></img>Buy Now
                </button>
                <button className= "trailer">Watch Trailer</button>
            </div>
        )
    }
    return; 

}

const LeftArrow = (props) => {
    return (
      <div className="backArrow arrow" onClick={props.goToPrevSlide}>
        <img src= "./chevron-left.svg"></img>
      </div>
    );
}

