class Slider extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            images: [
                "./huntforthe.jpg",
                "./nichts.jpg", 
                "./viceversa.jpg",
                "./fourthphase.jpg", 
                "./fullmoon.jpg"
            ], 
            content: [
                {
                    title: 'Hunt for the Wilderpeople',
                    description: 'Raised on hip-hop and foster care, defiant city kid Ricky gets a fresh start in the New Zealand countryside. From the director of What We Do In The Shadows.',
                    image: './huntforthe.jpg'
                },
                {
                    title: 'Nichts passiert/A Decent Man',
                    description: 'A Swiss family takes a ski vacation and runs into trouble when the father, the titular decent man, finds himself in a series of moral quandaries.',
                    image: './nichts.jpg'
                },
                {
                    title: 'Vice Versa',
                    description: 'Come along with the Good Company crew as they travel throughout the US, Japan, BC and Quebec to showcase skiing in the best way possible.',
                    image: './viceversa.jpg'
                },
                {
                    title: 'The Fourth Phase',
                    description: 'From the creators of The Art of FLIGHT, Red Bull Media House presents THE FOURTH PHASE, a snowboarding epic starring iconic athlete Travis Rice.',
                    image: './fourthphase.jpg'
                },
                {
                    title: 'Full Moon',
                    description: 'Be inspired by women who push boundaries. FULL MOON showcases the legends, current icons and future prodigies of this ever-evolving lifestyle sport.',
                    image: './fullmoon.jpg'
                }
            ],
            currentIndex: 0, 
            translateValue: 0
        }
    }

    goToPrevSlide = () => {
        if(this.state.currentIndex === 0) { //the first slide
            return; //cannot go to previous slide, TRY TO CHANGE!!!
        }
        this.setState(prevState => ({
            currentIndex: prevState.currentIndex - 1, //goes down
            translateValue: prevState.translateValue + this.slideWidth() 
        }))
    }

    goToNextSlide = () => {
        if(this.state.currentIndex === this.state.images.length - 1) {
            return this.setState({
                currentIndex: 0, 
                translateValue: 0
            })
        }
        this.setState(prevState => ({
            currentIndex: prevState.currentIndex + 1, 
            translateValue: prevState.translateValue + -(this.slideWidth())
        })); 
    }

    slideWidth = () => {
        return document.querySelector('.slide').clientWidth
    }

    render() {
        return (
            <div className= "slider">
                <div className= "slider-wrapper"
                    style= {{
                        transform: `translateX(${this.state.translateValue}px)`,
                        transition: "transform ease-out 0.3s"
                    }}>
                    {
                        this.state.content.map((item, i) => (
                            <div key={i} className= "slide-and-info">
                                <div className= "slide" style={
                                    {
                                        backgroundImage: `url('${item.image}')`,
                                        backgroundSize: 'cover',
                                        backgroundRepeat: 'no-repeat',
                                        backgroundPosition: '100% 50%',
                                    }
                                }></div>
                                <div className= "info">
                                    <img className= "poster" src={item.image}></img>
                                    <div className= "info-right">
                                        <h1>{item.title}</h1>
                                        <p>{item.description}</p>
                                        <button className= "buy">
                                            <img src= "./whitevimeo.png"></img>
                                            Buy Now
                                        </button>
                                        <button className= "trailer">
                                            Watch Trailer
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))
                    } 
                </div>
                <LeftArrow goToPrevSlide= {this.goToPrevSlide}/>
                <RightArrow goToNextSlide= {this.goToNextSlide}/>
            </div>  
        )   
    }
}

const LeftArrow = (props) => {
    return (
      <div className="backArrow arrow" onClick={props.goToPrevSlide}>
        <img src= "./left-chevron.svg"></img>
      </div>
    );
}

const RightArrow = (props) => {
    return (
        <div className="nextArrow arrow" onClick={props.goToNextSlide}>
            <img src= "./right-chevron.svg"></img> 
        </div>
    );
}

ReactDOM.render(<VimeoPage />, document.getElementById('root')); 
ReactDOM.render(<Slider />, document.getElementById('carousel'));
